import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

type Note = {
  note_id: number;
  customer_id: number;
  text: string;
  createdAt: Date;
  remindAt: Date;
  updatedAt: Date;
  status: boolean;

}

const App = () => {
  const [notes, setNotes] = useState<Note[]>([
  ]);

  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  const data = "";

  useEffect(() => {
    console.log('useEffect is called');
    const fetchNotes = async () => {
      try {
        console.log('test');
        const response = await fetch('http://localhost:3000/notes');
        console.log('hello');
        const data = await response.json();
        setNotes(data);
        console.log('data', data);
      } catch (error) {
        console.error('Error fetching notes', error);
      }
    }
    fetchNotes();
  });


  const handleNoteClick = async (note: Note) => {
    try {
      const response = await fetch(`http://localhost:3000/notes/${note.note_id}`);
      const data = await response.json();
      setSelectedNote(data);
      setTitle(data.text);
    } catch (error) {
      console.error('Error fetching note', error);
    }
  }

  const handleSubmit = (
    event: React.FormEvent) => {
      event.preventDefault();
      console.log("title : ", title);
      console.log("content : ", content);

      const newNote: Note = {
        note_id: notes.length + 1,
        customer_id: 1,
        text: title,
        createdAt: new Date(),
        remindAt: new Date(),
        updatedAt: new Date(),
        status: true
      };

      setNotes([newNote, ...notes]);
      setTitle('');
      setContent('');
    }

    const handleUpdateNote = (event : React.FormEvent) => {
      event.preventDefault();
      if(!selectedNote){
        return;
        };


    const updatedNote: Note = {
      customer_id: selectedNote.customer_id,
      note_id: selectedNote.note_id,
      text : title,
      createdAt: selectedNote.createdAt,
      remindAt: selectedNote.remindAt,
      updatedAt: new Date(),
      status: selectedNote.status
    }

    const updatedNotesList = notes.map((note) => note.note_id === selectedNote.note_id ? updatedNote : note)

    setNotes(updatedNotesList);
    setTitle('');
    setContent('');
    setSelectedNote(null);
  };

  const handleCancel = () => {
    setTitle('');
    setContent('');
    setSelectedNote(null);
  }

  const deleteNote = (event: React.MouseEvent, noteId : number) => {
    event.stopPropagation();

    const updatedNotes = notes.filter((note) => note.note_id !== noteId);

    setNotes(updatedNotes);
  }

  const handleStatusChange = async () => {
    if (!selectedNote) return;

    const updatedNote = { ...selectedNote, status: !selectedNote.status };

    try {
      const response = await fetch(`http://localhost:3000/notes/${updatedNote.note_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedNote),
      });

      if (!response.ok) throw new Error('Error updating note');

      const updatedNotesList = notes.map((note) =>
        note.note_id === updatedNote.note_id ? updatedNote : note
      );

      setNotes(updatedNotesList);
      setSelectedNote(updatedNote);
    } catch (error) {
      console.error('Error updating note', error);
    }
  };


  return (
    <div className="app-container">
      <form className = "note-form"
      onSubmit={(e) => handleSubmit(e)}>
        <input value={title} onChange={(e) => setTitle(e.target.value)}
        placeholder="Title" required>
        </input>
        <textarea value={content} onChange={(e) => setContent(e.target.value)} placeholder="Content" rows={10} required>

        </textarea>

        {selectedNote ? (
          <div className='edit-buttons'>
            <button type="submit">Save</button>
            <button onClick={handleCancel}>Cancel</button>
            <button onClick={handleStatusChange}>
      {selectedNote.status ? 'Mark as Finished' : 'Mark as Unfinished'}
    </button>
          </div> ) : (
            <button type="submit">Add Note</button>
          )}

{selectedNote && (
      <div className="selected-note">
        <h2>{selectedNote.text}</h2>
        <p>Created at: {selectedNote.createdAt.toString()}</p>
        <p>Updated at: {selectedNote.updatedAt.toString()}</p>
        <p>Status: {selectedNote.status ? 'Finished' : 'Unfinished'}</p>
      </div>
    )}


      </form>
      <div className ="notes-grid">
        { notes.map((note)=> (
          <div className="note-item" onClick={() => handleNoteClick(note)}>
          <div className="notes-header">
            <button onClick={(e) => deleteNote(e, note.note_id)}>x</button>
          </div>
          <h2>{note.text}</h2>
          <p></p>
        </div>
        ))}

      </div>
    </div>
  )
}

export default App;
