import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import DatePicker from "react-date-picker";

type Note = {
  note_id: number;
  customer_id: number;
  text: string;
  createdAt: Date;
  remindAt: Date;
  updatedAt: Date;
  status: boolean;
  category: Category;
};

type Category = {
  category_id: number;
  note_id: number;
  category_name: string;
};

const App = () => {
  const express = require("express");
  const app = express();

  app.use(express.json());
  const [notes, setNotes] = useState<Note[]>([]);

  const [title, setTitle] = useState("");
  const [text, setText] = useState("");
  const [categoryName, setCategoryName] = useState("");
  const [date, setDate] = useState("");
  const [status, setStatus] = useState(true);

  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  const data = "";

  useEffect(() => {
    // console.log("useEffect is called");
    const fetchNotes = async () => {
      try {
        // console.log("test");
        const response = await fetch("http://localhost:3000/notes");
        // console.log("hello");
        const data = await response.json();
        setNotes(data);
        // console.log("data", data);
      } catch (error) {
        console.error("Error fetching notes", error);
      }
    };
    fetchNotes();
    const intervalId = setInterval(fetchNotes, 5000);
    return () => clearInterval(intervalId);
  });

  const handleNoteClick = async (note: Note) => {
    try {
      const response = await fetch(
        `http://localhost:3000/notes/${note.note_id}`
      );
      const data = await response.json();
      setSelectedNote(data);
      setTitle(data.text);
      setText(data.text);
      setCategoryName(data.category.category_name);
      setDate(data.remindAt);
      setStatus(data.status);
    } catch (error) {
      console.error("Error fetching note", error);
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    try {
      console.log("text", text);
      console.log("categoryName", categoryName);
      console.log("ThisisRemindAt", date);
      const response = await fetch("http://localhost:3000/notes/create", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          customer_id: 1,
          text: text,
          remindAt: date,
          category_name: categoryName,
        }),
      });
      let newNote = await response.json();

      // Manually add the missing fields
      newNote = {
        ...newNote,
        createdAt: new Date(),
        updatedAt: new Date(),
        remindAt: new Date(date),
        category: { category_name: categoryName },
      };
      // const newNote = await response.json();
      setNotes([newNote, ...notes]);
      console.log("response", response);
    } catch (error) {
      console.error("Error creating note", error);
    }
  };

  const handleUpdateNote = (event: React.FormEvent) => {
    event.preventDefault();
    if (!selectedNote) {
      return;
    }
    setTitle("");
    setText("");
    setSelectedNote(null);
  };

  const handleCancel = () => {
    setText("");
    setDate("");
    setCategoryName("");
    setSelectedNote(null);
  };

  const deleteNote = (event: React.MouseEvent, noteId: number) => {
    event.stopPropagation();

    const updatedNotes = notes.filter((note) => note.note_id !== noteId);

    setNotes(updatedNotes);
  };
  type ValuePiece = Date | null;

  type Value = ValuePiece | [ValuePiece, ValuePiece];

  const [value, onChange] = useState<Value>(new Date());

  return (
    <div className="app-container">
      <form className="note-form" onSubmit={(e) => handleSubmit(e)}>
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Text"
          required
        ></input>

        <input
          value={categoryName}
          onChange={(e) => setCategoryName(e.target.value)}
          placeholder="Category Name"
          required
        ></input>
        <input
          value={date}
          onChange={(e) => setDate(e.target.value)}
          placeholder="Set Date Reminder"
          required
        ></input>

        {selectedNote ? (
          <div className="edit-buttons">
            <div className="checkbox-container">
              <label>
                Checkmark
                <input
                  type="checkbox"
                  checked={status}
                  onChange={(e) => setStatus(e.target.checked)}
                />
              </label>
            </div>
            <button type="submit">Save</button>
            <button onClick={handleCancel}>Cancel</button>
          </div>
        ) : (
          <button type="submit">Add Note</button>
        )}
      </form>

      {selectedNote && (
        <div className="selected-note">
          <h2>{selectedNote.text}</h2>
          <p>Created at: {selectedNote.createdAt.toString()}</p>
          <p>Updated at: {selectedNote.updatedAt.toString()}</p>
          <p>Status: {selectedNote.status ? "Finished" : "Unfinished"}</p>
        </div>
      )}

      <div className="notes-grid">
        {notes.map((note) => (
          <div className="note-item" onClick={() => handleNoteClick(note)}>
            <div className="notes-header">
              <button onClick={(e) => deleteNote(e, note.note_id)}>x</button>
            </div>
            <h2>{note.text}</h2>
            <p>Customer ID :{note.customer_id}</p>
            <p>Created At : {note.updatedAt.toString()}</p>
            <p>Remind At : {note.remindAt.toString()}</p>
            <p>Status : {note.status ? "Finished" : "Unfinished"}</p>
            <p>Category Name : {note.category.category_name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
